import React, { useState } from 'react';

const CadastroConsultor = ({ onVoltar, onCadastroSucesso }) => {
  const [formData, setFormData] = useState({
    nome: '',
    telefone: '',
    email: '',
    senha: '',
    confirmarSenha: '',
    cpf: '',
    pix: '',
    aceitaTermos: false
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const validateCPF = (cpf) => {
    cpf = cpf.replace(/[^\d]/g, '');
    if (cpf.length !== 11) return false;
    
    if (/^(\d)\1{10}$/.test(cpf)) return false;
    
    let sum = 0;
    for (let i = 0; i < 9; i++) {
      sum += parseInt(cpf.charAt(i)) * (10 - i);
    }
    let remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cpf.charAt(9))) return false;
    
    sum = 0;
    for (let i = 0; i < 10; i++) {
      sum += parseInt(cpf.charAt(i)) * (11 - i);
    }
    remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cpf.charAt(10))) return false;
    
    return true;
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^\(\d{2}\) \d{4,5}-\d{4}$/;
    return phoneRegex.test(phone);
  };

  const formatCPF = (value) => {
    value = value.replace(/\D/g, '');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d{1,2})$/, '$1-$2');
    return value;
  };

  const formatPhone = (value) => {
    value = value.replace(/\D/g, '');
    value = value.replace(/(\d{2})(\d)/, '($1) $2');
    value = value.replace(/(\d{4})(\d)/, '$1-$2');
    value = value.replace(/(\d{4})-(\d)(\d{4})/, '$1$2-$3');
    return value;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    let formattedValue = value;
    
    if (name === 'cpf') {
      formattedValue = formatCPF(value);
    } else if (name === 'telefone') {
      formattedValue = formatPhone(value);
    } else if (name === 'pix') {
      formattedValue = formatCPF(value);
    }
    
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : formattedValue
    }));
    
    // Limpar erro quando o usu√°rio come√ßa a digitar
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.nome.trim()) {
      newErrors.nome = 'Nome √© obrigat√≥rio';
    }
    
    if (!formData.telefone.trim()) {
      newErrors.telefone = 'Telefone √© obrigat√≥rio';
    } else if (!validatePhone(formData.telefone)) {
      newErrors.telefone = 'Telefone inv√°lido';
    }
    
    if (!formData.email.trim()) {
      newErrors.email = 'E-mail √© obrigat√≥rio';
    } else if (!validateEmail(formData.email)) {
      newErrors.email = 'E-mail inv√°lido';
    }
    
    if (!formData.senha) {
      newErrors.senha = 'Senha √© obrigat√≥ria';
    } else if (formData.senha.length < 6) {
      newErrors.senha = 'Senha deve ter pelo menos 6 caracteres';
    }
    
    if (!formData.confirmarSenha) {
      newErrors.confirmarSenha = 'Confirma√ß√£o de senha √© obrigat√≥ria';
    } else if (formData.senha !== formData.confirmarSenha) {
      newErrors.confirmarSenha = 'Senhas n√£o conferem';
    }
    
    if (!formData.cpf.trim()) {
      newErrors.cpf = 'CPF √© obrigat√≥rio';
    } else if (!validateCPF(formData.cpf)) {
      newErrors.cpf = 'CPF inv√°lido';
    }
    
    if (!formData.pix.trim()) {
      newErrors.pix = 'PIX √© obrigat√≥rio';
    } else if (!validateCPF(formData.pix)) {
      newErrors.pix = 'PIX deve ser um CPF v√°lido';
    } else if (formData.pix.replace(/\D/g, '') !== formData.cpf.replace(/\D/g, '')) {
      newErrors.pix = 'PIX deve ser igual ao CPF informado';
    }
    
    if (!formData.aceitaTermos) {
      newErrors.aceitaTermos = 'Voc√™ deve aceitar os termos de uso';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const response = await fetch('http://localhost:5000/api/consultores/cadastro', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nome: formData.nome,
          telefone: formData.telefone,
          email: formData.email,
          senha: formData.senha,
          cpf: formData.cpf.replace(/\D/g, ''),
          pix: formData.pix.replace(/\D/g, ''),
          tipo: 'consultor'
        })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        onCadastroSucesso();
      } else {
        setErrors({ general: data.error || 'Erro ao cadastrar consultor' });
      }
    } catch (error) {
      console.error('Erro no cadastro:', error);
      setErrors({ general: 'Erro de conex√£o. Tente novamente.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '2rem'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '20px',
        padding: '3rem',
        boxShadow: '0 30px 60px rgba(0, 0, 0, 0.2)',
        width: '100%',
        maxWidth: '600px'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h1 style={{
            fontSize: '2rem',
            fontWeight: '700',
            color: '#1a1d23',
            marginBottom: '0.5rem'
          }}>
            Cadastre-se como Consultor
          </h1>
          <p style={{
            fontSize: '1rem',
            color: '#6b7280'
          }}>
            Preencha os dados abaixo para come√ßar a ganhar dinheiro conosco
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gap: '1.5rem' }}>
            <div className="form-group">
              <label className="form-label">Nome Completo *</label>
              <input
                type="text"
                name="nome"
                className="form-input"
                placeholder="Digite seu nome completo"
                value={formData.nome}
                onChange={handleChange}
                style={{
                  borderColor: errors.nome ? '#ef4444' : '#d1d5db'
                }}
              />
              {errors.nome && (
                <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                  {errors.nome}
                </span>
              )}
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
              <div className="form-group">
                <label className="form-label">Telefone *</label>
                <input
                  type="text"
                  name="telefone"
                  className="form-input"
                  placeholder="(11) 99999-9999"
                  value={formData.telefone}
                  onChange={handleChange}
                  maxLength="15"
                  style={{
                    borderColor: errors.telefone ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.telefone && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.telefone}
                  </span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">E-mail *</label>
                <input
                  type="email"
                  name="email"
                  className="form-input"
                  placeholder="seu@email.com"
                  value={formData.email}
                  onChange={handleChange}
                  style={{
                    borderColor: errors.email ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.email && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.email}
                  </span>
                )}
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
              <div className="form-group">
                <label className="form-label">Senha *</label>
                <input
                  type="password"
                  name="senha"
                  className="form-input"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={formData.senha}
                  onChange={handleChange}
                  style={{
                    borderColor: errors.senha ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.senha && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.senha}
                  </span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Repetir Senha *</label>
                <input
                  type="password"
                  name="confirmarSenha"
                  className="form-input"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={formData.confirmarSenha}
                  onChange={handleChange}
                  style={{
                    borderColor: errors.confirmarSenha ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.confirmarSenha && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.confirmarSenha}
                  </span>
                )}
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
              <div className="form-group">
                <label className="form-label">CPF *</label>
                <input
                  type="text"
                  name="cpf"
                  className="form-input"
                  placeholder="000.000.000-00"
                  value={formData.cpf}
                  onChange={handleChange}
                  maxLength="14"
                  style={{
                    borderColor: errors.cpf ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.cpf && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.cpf}
                  </span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">PIX (CPF) *</label>
                <input
                  type="text"
                  name="pix"
                  className="form-input"
                  placeholder="000.000.000-00"
                  value={formData.pix}
                  onChange={handleChange}
                  maxLength="14"
                  style={{
                    borderColor: errors.pix ? '#ef4444' : '#d1d5db'
                  }}
                />
                {errors.pix && (
                  <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                    {errors.pix}
                  </span>
                )}
              </div>
            </div>

            <div className="form-group">
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input
                  type="checkbox"
                  name="aceitaTermos"
                  checked={formData.aceitaTermos}
                  onChange={handleChange}
                  style={{
                    width: '1.25rem',
                    height: '1.25rem',
                    accentColor: '#667eea'
                  }}
                />
                <span style={{ fontSize: '0.875rem', color: '#374151' }}>
                  Aceito os{' '}
                  <a href="#" style={{ color: '#667eea', textDecoration: 'underline' }}>
                    termos de uso
                  </a>{' '}
                  e{' '}
                  <a href="#" style={{ color: '#667eea', textDecoration: 'underline' }}>
                    pol√≠tica de privacidade
                  </a>
                </span>
              </label>
              {errors.aceitaTermos && (
                <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>
                  {errors.aceitaTermos}
                </span>
              )}
            </div>

            {errors.general && (
              <div style={{
                padding: '1rem',
                background: '#fee2e2',
                border: '1px solid #fecaca',
                borderRadius: '8px',
                color: '#dc2626',
                fontSize: '0.875rem'
              }}>
                {errors.general}
              </div>
            )}

            <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
              <button
                type="button"
                onClick={onVoltar}
                style={{
                  flex: '1',
                  padding: '0.75rem',
                  background: '#f3f4f6',
                  color: '#374151',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '1rem',
                  fontWeight: '500',
                  cursor: 'pointer'
                }}
              >
                Voltar
              </button>
              <button
                type="submit"
                disabled={loading}
                style={{
                  flex: '2',
                  padding: '0.75rem',
                  background: loading ? '#9ca3af' : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Cadastrando...' : 'Cadastrar'}
              </button>
            </div>
          </div>
        </form>

        <div style={{
          marginTop: '2rem',
          padding: '1rem',
          background: '#f0f9ff',
          borderRadius: '8px',
          border: '1px solid #bae6fd'
        }}>
          <h4 style={{ color: '#0369a1', fontSize: '0.875rem', fontWeight: '600', marginBottom: '0.5rem' }}>
            üí° Informa√ß√µes importantes:
          </h4>
          <ul style={{ color: '#0369a1', fontSize: '0.75rem', margin: 0, paddingLeft: '1rem' }}>
            <li>Seu PIX deve ser o mesmo CPF informado</li>
            <li>Voc√™ receber√° R$ 5 de comiss√£o a cada R$ 1.000 fechados</li>
            <li>Seu login ser√° feito com o e-mail informado</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default CadastroConsultor;
