                  <option value="Odontol√≥gico">Tratamento Odontol√≥gico</option>
                  <option value="Ambos">Ambos os Tratamentos</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">CPF *</label>
                <input
