                  name="cpf"
                  className={`form-input ${errors.cpf ? 'error' : ''}`}
                  value={formData.cpf}
                  onChange={handleInputChange}
                  placeholder="000.000.000-00"
                  disabled={loading}
                  maxLength="14"
