import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';

const Consultores = () => {
  const { makeRequest, isAdmin } = useAuth();
  const [consultores, setConsultores] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingConsultor, setEditingConsultor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [showSenhaModal, setShowSenhaModal] = useState(false);
  const [consultorSenha, setConsultorSenha] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    telefone: '',
    senha: ''
  });

  const fetchConsultores = useCallback(async () => {
    try {
      const response = await makeRequest('/consultores');
      const data = await response.json();

      if (response.ok) {
        setConsultores(data);
      } else {
        console.error('Erro ao carregar consultores:', data.error);
        setMessage('Erro ao carregar consultores: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao carregar consultores:', error);
      setMessage('Erro ao conectar com o servidor');
    } finally {
      setLoading(false);
    }
  }, [makeRequest]);

  useEffect(() => {
    fetchConsultores();
  }, [fetchConsultores]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let response;
      if (editingConsultor) {
        response = await makeRequest(`/consultores/${editingConsultor.id}`, {
          method: 'PUT',
          body: JSON.stringify(formData)
        });
      } else {
        response = await makeRequest('/consultores', {
          method: 'POST',
          body: JSON.stringify(formData)
        });
      }

      const data = await response.json();

      if (response.ok) {
        setMessage(editingConsultor ? 'Consultor atualizado com sucesso!' : 'Consultor cadastrado com sucesso!');
        setShowModal(false);
        setEditingConsultor(null);
        setFormData({
          nome: '',
          telefone: '',
          senha: ''
        });
        fetchConsultores();
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Erro ao salvar consultor: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao salvar consultor:', error);
      setMessage('Erro ao salvar consultor');
    }
  };

  const handleEdit = (consultor) => {
    setEditingConsultor(consultor);
    setFormData({
      nome: consultor.nome || '',
      telefone: consultor.telefone || '',
      senha: consultor.senha || ''
    });
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    const numbers = telefone.replace(/\D/g, '');
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      telefone: '',
      senha: ''
    });
    setEditingConsultor(null);
    setShowModal(false);
  };

  const visualizarSenha = async (consultor) => {
    try {
      const response = await makeRequest(`/consultores/${consultor.id}`);
      const data = await response.json();

      if (response.ok) {
        setConsultorSenha({
          ...consultor,
          temSenha: !!data.senha,
          hashSenha: data.senha
        });
        setShowSenhaModal(true);
      } else {
        setMessage('Erro ao carregar dados do consultor: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao carregar consultor:', error);
      setMessage('Erro ao conectar com o servidor');
    }
  };

  const redefinirSenha = async (consultorId, novaSenha) => {
    try {
      const consultor = consultores.find(c => c.id === consultorId);
      const response = await makeRequest(`/consultores/${consultorId}`, {
        method: 'PUT',
        body: JSON.stringify({
          nome: consultor.nome,
          telefone: consultor.telefone,
          senha: novaSenha
        })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Senha redefinida com sucesso!');
        setShowSenhaModal(false);
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Erro ao redefinir senha: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao redefinir senha:', error);
      setMessage('Erro ao redefinir senha');
    }
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">Consultores</h1>
        <p className="page-subtitle">Gerencie a equipe de consultores</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Equipe de Consultores</h2>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M12 5v14M5 12h14" />
            </svg>
            Novo Consultor
          </button>
        </div>

        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
          </div>
        ) : consultores.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#6b7280', padding: '2rem' }}>
            Nenhum consultor cadastrado ainda.
          </p>
        ) : (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Email de Acesso</th>
                  <th>Telefone</th>
                  <th>Data de Cadastro</th>
                  <th>A√ß√µes</th>
                </tr>
              </thead>
              <tbody>
                {consultores.map(consultor => (
                  <tr key={consultor.id}>
                    <td>
                      <strong>{consultor.nome}</strong>
                    </td>
                    <td>
                      <span style={{ fontFamily: 'monospace', fontSize: '0.9rem', color: '#2563eb' }}>
                        {consultor.email || `${consultor.nome?.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, '').replace(/[^a-z0-9]/g, '')}@investmoneysa.com.br`}
                      </span>
                    </td>
                    <td>
                      {consultor.telefone ? formatarTelefone(consultor.telefone) : '-'}
                    </td>
                    <td>
                      {formatarData(consultor.created_at)}
                    </td>
                    <td>
                      <div style={{ display: 'flex', gap: '0.5rem' }}>
                        <button
                          onClick={() => handleEdit(consultor)}
                          className="btn-action"
                          title="Editar"
                        >
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" />
                            <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z" />
                          </svg>
                        </button>
                        {isAdmin && (
                          <button
                            onClick={() => visualizarSenha(consultor)}
                            className="btn-action"
                            title="Gerenciar senha"
                          >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <rect x="3" y="11" width="18" height="11" rx="2" ry="2" />
                              <path d="M7 11V7a5 5 0 0 1 10 0v4" />
                            </svg>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Modal de Cadastro/Edi√ß√£o */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingConsultor ? 'Editar Consultor' : 'Novo Consultor'}
              </h2>
              <button 
                className="close-btn"
                onClick={resetForm}
              >
                √ó
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome Completo *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome do consultor"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Telefone</label>
                <input
                  type="tel"
                  name="telefone"
                  className="form-input"
                  value={formData.telefone}
                  onChange={handleInputChange}
                  placeholder="(11) 99999-9999"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Senha</label>
                <input
                  type="password"
                  name="senha"
                  className="form-input"
                  value={formData.senha}
                  onChange={handleInputChange}
                  placeholder="Digite a senha do consultor"
                />
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancelar
                </button>
                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  {editingConsultor ? 'üíæ Atualizar Consultor' : 'üíæ Cadastrar Consultor'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Modal de Visualiza√ß√£o/Altera√ß√£o de Senha */}
      {showSenhaModal && consultorSenha && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                üîê Senha de {consultorSenha.nome}
              </h2>
              <button 
                className="close-btn"
                onClick={() => setShowSenhaModal(false)}
              >
                ‚úï
              </button>
            </div>

            <div style={{ padding: '1rem 0' }}>
              {/* Informa√ß√µes do Consultor */}
              <div style={{ 
                background: '#f8fafc', 
                padding: '1.5rem', 
                borderRadius: '12px',
                marginBottom: '2rem',
                border: '1px solid #e2e8f0'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', marginBottom: '1rem' }}>
                  <div style={{
                    background: '#667eea',
                    color: 'white',
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '1.5rem',
                    marginRight: '1rem'
                  }}>
                    ü©∫
                  </div>
                  <div>
                    <h3 style={{ margin: 0, fontSize: '1.2rem', color: '#2d3748' }}>
                      {consultorSenha.nome}
                    </h3>
                    <p style={{ margin: 0, color: '#718096', fontSize: '0.9rem' }}>
                      Email: {consultorSenha.email || 'N√£o definido'}
                    </p>
                  </div>
                </div>

                {/* Status da Senha */}
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center',
                  padding: '1rem',
                  borderRadius: '8px',
                  background: consultorSenha.temSenha ? '#ecfdf5' : '#fef2f2',
                  border: `1px solid ${consultorSenha.temSenha ? '#d1fae5' : '#fecaca'}`
                }}>
                  <span style={{ fontSize: '1.5rem', marginRight: '0.75rem' }}>
                    {consultorSenha.temSenha ? '‚úÖ' : '‚ùå'}
                  </span>
                  <div>
                    <strong style={{ 
                      color: consultorSenha.temSenha ? '#065f46' : '#dc2626',
                      fontSize: '1rem'
                    }}>
                      {consultorSenha.temSenha ? 'Senha Configurada' : 'Sem Senha'}
                    </strong>
                    <p style={{ 
                      margin: 0, 
                      fontSize: '0.85rem',
                      color: consultorSenha.temSenha ? '#047857' : '#b91c1c'
                    }}>
                      {consultorSenha.temSenha 
                        ? 'Este consultor pode fazer login no sistema' 
                        : 'Este consultor n√£o pode fazer login (defina uma senha)'
                      }
                    </p>
                  </div>
                </div>
              </div>

              {/* Formul√°rio para Nova Senha */}
              <div>
                <h4 style={{ 
                  fontSize: '1.1rem', 
                  marginBottom: '1rem',
                  color: '#2d3748',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem'
                }}>
                  üîÑ {consultorSenha.temSenha ? 'Alterar Senha' : 'Definir Senha'}
                </h4>

                <form onSubmit={(e) => {
                  e.preventDefault();
                  const novaSenha = e.target.novaSenha.value;
                  if (novaSenha.length < 3) {
                    alert('A senha deve ter pelo menos 3 caracteres');
                    return;
                  }
                  if (window.confirm(`Tem certeza que deseja ${consultorSenha.temSenha ? 'alterar' : 'definir'} a senha de ${consultorSenha.nome}?`)) {
                    redefinirSenha(consultorSenha.id, novaSenha);
                  }
                }}>
                  <div className="form-group">
                    <label className="form-label">Nova Senha *</label>
                    <input
                      type="password"
                      name="novaSenha"
                      className="form-input"
                      placeholder="Digite a nova senha (m√≠nimo 3 caracteres)"
                      required
                      minLength="3"
                      style={{ fontSize: '1rem', padding: '0.75rem' }}
                    />
                    <small style={{ color: '#718096', fontSize: '0.85rem' }}>
                      üí° Dica: Use uma senha simples e f√°cil de lembrar (ex: 123456)
                    </small>
                  </div>

                  <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end', marginTop: '2rem' }}>
                    <button 
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => setShowSenhaModal(false)}
                    >
                      Cancelar
                    </button>
                    <button 
                      type="submit"
                      className="btn"
                      style={{ 
                        background: '#f59e0b',
                        color: 'white',
                        border: 'none',
                        borderRadius: '6px',
                        padding: '0.75rem 1.5rem'
                      }}
                    >
                      üîê {consultorSenha.temSenha ? 'Alterar Senha' : 'Definir Senha'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Consultores;
