              </select>
            </div>

            {/* Bot√£o Limpar Filtros */}
            {(filtroEstado || filtroCity) && (
              <div className="form-group" style={{ margin: 0 }}>
                <label style={{ 
                  fontSize: '1rem', 
                  color: 'transparent', 
                  marginBottom: '0.5rem',
                  fontWeight: '500',
                  display: 'block'
                }}>
                  .
                </label>
                <button 
                  onClick={() => {
                    setFiltroEstado('');
                    setFiltroCity('');
                  }}
                  className="btn btn-secondary"
                  style={{ 
                    padding: '0.875rem 1.5rem',
                    fontSize: '1rem',
                    borderRadius: '12px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    width: '100%',
                    justifyContent: 'center'
                  }}
                >
                  üóëÔ∏è Limpar Filtros
                </button>
              </div>
            )}
          </div>

          {/* Contador de resultados */}
          {(filtroEstado || filtroCity) && (
