                      {clinica.estado && (
                        <div style={{ display: 'flex', alignItems: 'center' }}>
                          <span style={{ marginRight: '0.25rem' }}>üó∫Ô∏è</span>
                          <span style={{ 
                            color: '#059669', 
                            fontWeight: '600',
                            padding: '0.25rem 0.5rem',
                            background: '#d1fae5',
                            borderRadius: '12px',
