import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Consultores = () => {
  const [consultores, setConsultores] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    nome: '',
    telefone: ''
  });

  useEffect(() => {
    fetchConsultores();
  }, []);

  const fetchConsultores = async () => {
    try {
      const response = await axios.get('/api/consultores');
      setConsultores(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar consultores:', error);
      setLoading(false);
    }
  };



  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/consultores', formData);
      setMessage('Consultor cadastrado com sucesso!');
      setShowModal(false);
      setFormData({
        nome: '',
        email: '',
        telefone: '',
        clinica_id: ''
      });
      fetchConsultores();
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Erro ao cadastrar consultor:', error);
      setMessage('Erro ao cadastrar consultor');
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    const numbers = telefone.replace(/\D/g, '');
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">ü©∫ Gest√£o de Consultores</h1>
        <p className="page-subtitle">Cadastre os consultores da sua empresa</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">üë®‚Äç‚öïÔ∏è Lista de Consultores</h2>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
          >
            ‚ûï Novo Consultor
          </button>
        </div>

        {loading ? (
          <p>Carregando consultores...</p>
        ) : consultores.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            Nenhum consultor cadastrado ainda. Clique em "Novo Consultor" para come√ßar.
          </p>
        ) : (
          <div className="grid grid-2">
            {consultores.map(consultor => (
              <div key={consultor.id} className="card" style={{ margin: 0 }}>
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  marginBottom: '1rem',
                  paddingBottom: '0.75rem',
                  borderBottom: '2px solid #667eea'
                }}>
                  <span style={{ fontSize: '2rem', marginRight: '0.75rem' }}>ü©∫</span>
                  <h3 style={{ 
                    fontSize: '1.25rem', 
                    fontWeight: '600', 
                    color: '#2d3748',
                    margin: 0
                  }}>
                    {consultor.nome}
                  </h3>
                </div>
                
                <div style={{ display: 'grid', gap: '0.75rem' }}>
                  {consultor.telefone && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ marginRight: '0.5rem' }}>üìû</span>
                      <span style={{ color: '#4a5568' }}>{formatarTelefone(consultor.telefone)}</span>
                    </div>
                  )}
                  
                  {consultor.email && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ marginRight: '0.5rem' }}>üìß</span>
                      <a 
                        href={`mailto:${consultor.email}`}
                        style={{ color: '#667eea', textDecoration: 'none' }}
                      >
                        {consultor.email}
                      </a>
                    </div>
                  )}
                  
                  {consultor.clinica_nome && (
                    <div style={{ 
                      display: 'flex', 
                      alignItems: 'center',
                      padding: '0.5rem',
                      background: '#f0fff4',
                      borderRadius: '6px',
                      border: '1px solid #4CAF50'
                    }}>
                      <span style={{ marginRight: '0.5rem' }}>üè•</span>
                      <span style={{ color: '#047857', fontWeight: '500' }}>
                        {consultor.clinica_nome}
                      </span>
                    </div>
                  )}
                  
                  <div style={{ 
                    marginTop: '1rem',
                    paddingTop: '0.75rem',
                    borderTop: '1px solid #e2e8f0',
                    fontSize: '0.85rem',
                    color: '#718096'
                  }}>
                    Cadastrado em: {formatarData(consultor.created_at)}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Modal de Cadastro */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">ü©∫ Novo Consultor</h2>
              <button 
                className="close-btn"
                onClick={() => setShowModal(false)}
              >
                ‚úï
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome Completo *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome do consultor"
                  required
                />
              </div>

              <div className="grid grid-2">
                <div className="form-group">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="consultor@exemplo.com"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Telefone</label>
                  <input
                    type="tel"
                    name="telefone"
                    className="form-input"
                    value={formData.telefone}
                    onChange={handleInputChange}
                    placeholder="(11) 99999-9999"
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Cl√≠nica</label>
                <select
                  name="clinica_id"
                  className="form-select"
                  value={formData.clinica_id}
                  onChange={handleInputChange}
                >
                  <option value="">Selecione uma cl√≠nica</option>
                  {clinicas.map(clinica => (
                    <option key={clinica.id} value={clinica.id}>
                      {clinica.nome}
                    </option>
                  ))}
                </select>
                {clinicas.length === 0 && (
                  <p style={{ 
                    fontSize: '0.85rem', 
                    color: '#718096', 
