import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

const Pacientes = () => {
  const { makeRequest } = useAuth();
  const [pacientes, setPacientes] = useState([]);
  const [novosLeads, setNovosLeads] = useState([]);
  const [consultores, setConsultores] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingPaciente, setEditingPaciente] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('pacientes');
  const [formData, setFormData] = useState({
    nome: '',
    telefone: '',
    cpf: '',
    tipo_tratamento: '',
    status: 'lead',
    observacoes: '',
    consultor_id: ''
  });

  // Status disponÃ­veis para o pipeline
  const statusOptions = [
    { value: 'lead', label: 'ğŸ” Lead', color: '#fbbf24' },
    { value: 'agendado', label: 'ğŸ“… Agendado', color: '#60a5fa' },
    { value: 'compareceu', label: 'âœ… Compareceu', color: '#34d399' },
    { value: 'fechado', label: 'ğŸ’° Fechado', color: '#10b981' },
    { value: 'nao_fechou', label: 'âŒ NÃ£o Fechou', color: '#ef4444' },
    { value: 'nao_compareceu', label: 'ğŸš« NÃ£o Compareceu', color: '#f87171' },
    { value: 'reagendado', label: 'ğŸ”„ Reagendado', color: '#a78bfa' }
  ];

  useEffect(() => {
    fetchPacientes();
    fetchConsultores();
  }, []);

  const fetchPacientes = async () => {
    try {
      const response = await makeRequest('/pacientes');
      const data = await response.json();

      if (response.ok) {
        setPacientes(data);
      } else {
        console.error('Erro ao carregar pacientes:', data.error);
        setMessage('Erro ao carregar pacientes: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao carregar pacientes:', error);
      setMessage('Erro ao conectar com o servidor');
    } finally {
      setLoading(false);
    }
  };

  const fetchConsultores = async () => {
    try {
      const response = await makeRequest('/consultores');
      const data = await response.json();

      if (response.ok) {
        setConsultores(data);
      } else {
        console.error('Erro ao carregar consultores:', data.error);
      }
    } catch (error) {
      console.error('Erro ao carregar consultores:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let response;
      if (editingPaciente) {
        response = await makeRequest(`/pacientes/${editingPaciente.id}`, {
          method: 'PUT',
          body: JSON.stringify(formData)
        });
      } else {
        response = await makeRequest('/pacientes', {
          method: 'POST',
          body: JSON.stringify(formData)
        });
      }

      const data = await response.json();

      if (response.ok) {
        setMessage(editingPaciente ? 'Paciente atualizado com sucesso!' : 'Paciente cadastrado com sucesso!');
        setShowModal(false);
        setEditingPaciente(null);
        setFormData({
          nome: '',
          telefone: '',
          cpf: '',
          tipo_tratamento: '',
          status: 'lead',
          observacoes: '',
          consultor_id: ''
        });
        fetchPacientes();
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Erro ao salvar paciente: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao salvar paciente:', error);
      setMessage('Erro ao salvar paciente');
    }
  };

  const handleEdit = (paciente) => {
    setEditingPaciente(paciente);
    setFormData({
      nome: paciente.nome || '',
      telefone: paciente.telefone || '',
      cpf: paciente.cpf || '',
      tipo_tratamento: paciente.tipo_tratamento || '',
      status: paciente.status || 'lead',
      observacoes: paciente.observacoes || '',
      consultor_id: paciente.consultor_id || ''
    });
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const updateStatus = async (pacienteId, newStatus) => {
    try {
      const response = await makeRequest(`/pacientes/${pacienteId}/status`, {
        method: 'PUT',
        body: JSON.stringify({ status: newStatus })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Status atualizado com sucesso!');
        fetchPacientes();
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Erro ao atualizar status: ' + data.error);
      }
    } catch (error) {
      console.error('Erro ao atualizar status:', error);
      setMessage('Erro ao atualizar status');
    }
  };

  const getStatusInfo = (status) => {
    return statusOptions.find(option => option.value === status) || statusOptions[0];
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    // Remove caracteres nÃ£o numÃ©ricos
    const numbers = telefone.replace(/\D/g, '');
    // Aplica mÃ¡scara (xx) xxxxx-xxxx
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  const formatarCPF = (cpf) => {
    if (!cpf) return '';
    // Remove caracteres nÃ£o numÃ©ricos
    const numbers = cpf.replace(/\D/g, '');
    // Aplica mÃ¡scara xxx.xxx.xxx-xx
    if (numbers.length === 11) {
      return `${numbers.substring(0, 3)}.${numbers.substring(3, 6)}.${numbers.substring(6, 9)}-${numbers.substring(9)}`;
    }
    return cpf;
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      telefone: '',
      cpf: '',
      tipo_tratamento: '',
      status: 'lead',
      observacoes: '',
      consultor_id: ''
    });
    setEditingPaciente(null);
    setShowModal(false);
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">ğŸ‘¥ GestÃ£o de Pacientes</h1>
        <p className="page-subtitle">Cadastre e acompanhe seus pacientes e leads</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      {/* Resumo de EstatÃ­sticas */}
      <div style={{ 
        background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)', 
        padding: '2rem', 
        borderRadius: '16px', 
        marginBottom: '2rem',
        border: '1px solid #cbd5e0',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '1.5rem' 
        }}>
          <h3 style={{ 
            fontSize: '1.2rem', 
            fontWeight: '600', 
            color: '#2d3748', 
            margin: 0,
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            ğŸ“Š Resumo do Pipeline
          </h3>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
            style={{ 
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              fontWeight: '600'
            }}
          >
            â• Novo Paciente
          </button>
        </div>

        <div className="stats-grid" style={{ gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1rem' }}>
          <div style={{ 
            background: 'white', 
            padding: '1.5rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #fbbf24'
          }}>
            <div style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#fbbf24', marginBottom: '0.5rem' }}>
              {pacientes.filter(p => p.status === 'lead').length}
            </div>
            <div style={{ color: '#718096', fontSize: '0.9rem', fontWeight: '500' }}>ğŸ” Leads</div>
          </div>

          <div style={{ 
            background: 'white', 
            padding: '1.5rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #60a5fa'
          }}>
            <div style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#60a5fa', marginBottom: '0.5rem' }}>
              {pacientes.filter(p => p.status === 'agendado').length}
            </div>
            <div style={{ color: '#718096', fontSize: '0.9rem', fontWeight: '500' }}>ğŸ“… Agendados</div>
          </div>

          <div style={{ 
            background: 'white', 
            padding: '1.5rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #10b981'
          }}>
            <div style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#10b981', marginBottom: '0.5rem' }}>
              {pacientes.filter(p => p.status === 'fechado').length}
            </div>
            <div style={{ color: '#718096', fontSize: '0.9rem', fontWeight: '500' }}>ğŸ’° Fechados</div>
          </div>

          <div style={{ 
            background: 'white', 
            padding: '1.5rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #667eea'
          }}>
            <div style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#667eea', marginBottom: '0.5rem' }}>
              {pacientes.length}
            </div>
            <div style={{ color: '#718096', fontSize: '0.9rem', fontWeight: '500' }}>ğŸ‘¥ Total</div>
          </div>

          <div style={{ 
            background: 'white', 
            padding: '1.5rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #34d399'
          }}>
            <div style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#34d399', marginBottom: '0.5rem' }}>
              {pacientes.length > 0 
                ? Math.round((pacientes.filter(p => p.status === 'fechado').length / pacientes.length) * 100)
                : 0}%
            </div>
            <div style={{ color: '#718096', fontSize: '0.9rem', fontWeight: '500' }}>ğŸ“ˆ Taxa ConversÃ£o</div>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">ğŸ“‹ Lista Completa de Pacientes</h2>
          <div style={{ fontSize: '0.9rem', color: '#718096' }}>
            {pacientes.length} paciente(s) cadastrado(s)
          </div>
        </div>

        {loading ? (
          <p>Carregando pacientes...</p>
        ) : pacientes.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            Nenhum paciente cadastrado ainda. Clique em "Novo Paciente" para comeÃ§ar.
          </p>
        ) : (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Consultor</th>
                  <th>Telefone</th>
                  <th>CPF</th>
                  <th>Tipo de Tratamento</th>
                  <th>Status</th>
                  <th>Cadastrado em</th>
                  <th>AÃ§Ãµes</th>
                </tr>
              </thead>
              <tbody>
                {pacientes.map(paciente => {
                  const statusInfo = getStatusInfo(paciente.status);
