import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Consultores = () => {
  const [consultores, setConsultores] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingConsultor, setEditingConsultor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    nome: '',
    telefone: ''
  });

  useEffect(() => {
    fetchConsultores();
  }, []);

  const fetchConsultores = async () => {
    try {
      const response = await axios.get('/api/consultores');
      setConsultores(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar consultores:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingConsultor) {
        await axios.put(`/api/consultores/${editingConsultor.id}`, formData);
        setMessage('Consultor atualizado com sucesso!');
      } else {
        await axios.post('/api/consultores', formData);
        setMessage('Consultor cadastrado com sucesso!');
      }
      
      setShowModal(false);
      setEditingConsultor(null);
      setFormData({
        nome: '',
        telefone: ''
      });
      fetchConsultores();
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Erro ao salvar consultor:', error);
      setMessage('Erro ao salvar consultor');
    }
  };

  const handleEdit = (consultor) => {
    setEditingConsultor(consultor);
    setFormData({
      nome: consultor.nome || '',
      telefone: consultor.telefone || ''
    });
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    const numbers = telefone.replace(/\D/g, '');
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      telefone: ''
    });
    setEditingConsultor(null);
    setShowModal(false);
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">ü©∫ Gest√£o de Consultores</h1>
        <p className="page-subtitle">Cadastre os consultores da sua empresa</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      {/* Resumo de Informa√ß√µes */}
      <div style={{ 
        background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)', 
        padding: '2rem', 
        borderRadius: '16px', 
        marginBottom: '2rem',
        border: '1px solid #cbd5e0',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '1.5rem' 
        }}>
          <h3 style={{ 
            fontSize: '1.2rem', 
            fontWeight: '600', 
            color: '#2d3748', 
            margin: 0,
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            üìä Informa√ß√µes da Equipe
          </h3>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
            style={{ 
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              fontWeight: '600'
            }}
          >
            ‚ûï Novo Consultor
          </button>
        </div>
        
        <div className="stats-grid" style={{ gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1.5rem' }}>
          <div style={{ 
            background: 'white', 
            padding: '2rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #667eea'
          }}>
            <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#667eea', marginBottom: '0.5rem' }}>
              {consultores.length}
            </div>
            <div style={{ color: '#718096', fontSize: '1rem', fontWeight: '500' }}>ü©∫ Total de Consultores</div>
          </div>
          
          <div style={{ 
            background: 'white', 
            padding: '2rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #10b981'
          }}>
            <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#10b981', marginBottom: '0.5rem' }}>
              {consultores.filter(c => c.telefone && c.telefone.trim() !== '').length}
            </div>
            <div style={{ color: '#718096', fontSize: '1rem', fontWeight: '500' }}>üìû Com Telefone</div>
          </div>
          
          <div style={{ 
            background: 'white', 
            padding: '2rem', 
            borderRadius: '12px', 
            textAlign: 'center',
            border: '1px solid #e2e8f0',
            borderLeft: '4px solid #fbbf24'
          }}>
            <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#fbbf24', marginBottom: '0.5rem' }}>
              {consultores.length > 0 
                ? Math.round((consultores.filter(c => c.telefone && c.telefone.trim() !== '').length / consultores.length) * 100)
                : 0}%
            </div>
            <div style={{ color: '#718096', fontSize: '1rem', fontWeight: '500' }}>üìà Taxa Contato</div>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">üë®‚Äç‚öïÔ∏è Equipe de Consultores</h2>
          <div style={{ fontSize: '0.9rem', color: '#718096' }}>
            {consultores.length} consultor(es) cadastrado(s)
          </div>
        </div>

        {loading ? (
          <p>Carregando consultores...</p>
        ) : consultores.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            Nenhum consultor cadastrado ainda. Clique em "Novo Consultor" para come√ßar.
          </p>
        ) : (
          <div className="grid grid-2">
            {consultores.map(consultor => (
              <div key={consultor.id} className="card" style={{ margin: 0, position: 'relative' }}>
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  marginBottom: '1rem',
                  paddingBottom: '0.75rem',
                  borderBottom: '2px solid #667eea'
                }}>
                  <span style={{ fontSize: '2rem', marginRight: '0.75rem' }}>ü©∫</span>
                  <h3 style={{ 
                    fontSize: '1.25rem', 
                    fontWeight: '600', 
                    color: '#2d3748',
                    margin: 0,
                    flex: 1
                  }}>
                    {consultor.nome}
                  </h3>
                  <button
                    onClick={() => handleEdit(consultor)}
                    className="btn btn-secondary"
                    style={{ 
                      padding: '0.5rem', 
                      fontSize: '0.85rem',
                      minWidth: 'auto'
                    }}
                  >
                    ‚úèÔ∏è
                  </button>
                </div>
                
                <div style={{ display: 'grid', gap: '0.75rem' }}>
                  {consultor.telefone && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ marginRight: '0.5rem' }}>üìû</span>
                      <span style={{ color: '#4a5568' }}>{formatarTelefone(consultor.telefone)}</span>
                    </div>
                  )}
                  
                  <div style={{ 
                    marginTop: '1rem',
                    paddingTop: '0.75rem',
                    borderTop: '1px solid #e2e8f0',
                    fontSize: '0.85rem',
                    color: '#718096'
                  }}>
                    Cadastrado em: {formatarData(consultor.created_at)}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Modal de Cadastro/Edi√ß√£o */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingConsultor ? '‚úèÔ∏è Editar Consultor' : 'ü©∫ Novo Consultor'}
              </h2>
              <button 
                className="close-btn"
                onClick={resetForm}
              >
                ‚úï
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome Completo *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome do consultor"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Telefone</label>
                <input
                  type="tel"
                  name="telefone"
                  className="form-input"
                  value={formData.telefone}
                  onChange={handleInputChange}
                  placeholder="(11) 99999-9999"
                />
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancelar
                </button>
                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  {editingConsultor ? 'üíæ Atualizar Consultor' : 'üíæ Cadastrar Consultor'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Consultores;
