import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

const Dashboard = () => {
  const { makeRequest, isAdmin, user } = useAuth();
  const [stats, setStats] = useState({
    agendamentosHoje: 0,
    lembradosHoje: 0,
    totalPacientes: 0,
    fechamentosHoje: 0,
    fechamentosMes: 0,
    valorTotalMes: 0,
    ticketMedio: 0,
    totalFechamentos: 0,
    estatisticasConsultores: []
  });
  const [pipelineStats, setPipelineStats] = useState({
    leads: 0,
    agendados: 0,
    compareceram: 0,
    fechados: 0,
    naoFecharam: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
    fetchPipelineStats();
  }, []);

  const fetchDashboardData = async () => {
    try {
      console.log('ğŸ”„ Carregando dados do dashboard...');
      const response = await makeRequest('/dashboard');
      const data = await response.json();

      if (response.ok) {
        console.log('âœ… Dados do dashboard carregados:', data);
        setStats(data);
      } else {
        console.error('âŒ Erro ao carregar dashboard:', data.error);
        // Mostrar erro mais detalhado se for problema com fechamentos
        if (data.error && data.error.includes('fechamentos')) {
          alert('âš ï¸ Tabela de fechamentos nÃ£o encontrada. Execute a migraÃ§Ã£o no Supabase:\n\nALTER TABLE fechamentos ADD COLUMN IF NOT EXISTS contrato_arquivo TEXT;\nALTER TABLE fechamentos ADD COLUMN IF NOT EXISTS contrato_nome_original TEXT;\nALTER TABLE fechamentos ADD COLUMN IF NOT EXISTS contrato_tamanho INTEGER;\nALTER TABLE fechamentos ADD COLUMN IF NOT EXISTS contrato_upload_data TIMESTAMP DEFAULT NOW();');
        }
      }
    } catch (error) {
      console.error('âŒ Erro ao carregar dashboard:', error);
      // Tentar carregar dados parciais se der erro
      try {
        console.log('ğŸ”„ Tentando carregar dados bÃ¡sicos...');
        const pacientesResponse = await makeRequest('/pacientes');
        const agendamentosResponse = await makeRequest('/agendamentos');

        if (pacientesResponse.ok && agendamentosResponse.ok) {
          const pacientes = await pacientesResponse.json();
          const agendamentos = await agendamentosResponse.json();

          const hoje = new Date().toISOString().split('T')[0];
          const agendamentosHoje = agendamentos.filter(a => a.data_agendamento === hoje).length;
          const lembradosHoje = agendamentos.filter(a => a.data_agendamento === hoje && a.lembrado).length;

          setStats({
            agendamentosHoje,
            lembradosHoje,
            totalPacientes: pacientes.length,
            fechamentosHoje: 0,
            fechamentosMes: 0,
            valorTotalMes: 0,
            ticketMedio: 0,
            totalFechamentos: 0,
            estatisticasConsultores: []
          });

          console.log('âš ï¸ Carregados dados bÃ¡sicos. Fechamentos indisponÃ­veis.');
        }
      } catch (fallbackError) {
        console.error('âŒ Erro ao carregar dados bÃ¡sicos:', fallbackError);
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchPipelineStats = async () => {
    try {
      // Buscar estatÃ­sticas de pacientes por status
      const pacientesResponse = await makeRequest('/pacientes');
      const pacientes = await pacientesResponse.json();

      // Buscar estatÃ­sticas de agendamentos por status
      const agendamentosResponse = await makeRequest('/agendamentos');
      const agendamentos = await agendamentosResponse.json();

      // Contar por status
      const pipelineData = {
        leads: pacientes.filter(p => p.status === 'lead').length,
        agendados: pacientes.filter(p => p.status === 'agendado').length + 
                  agendamentos.filter(a => a.status === 'agendado').length,
        compareceram: pacientes.filter(p => p.status === 'compareceu').length + 
                     agendamentos.filter(a => a.status === 'compareceu').length,
        fechados: pacientes.filter(p => p.status === 'fechado').length + 
                 agendamentos.filter(a => a.status === 'fechado').length,
        naoFecharam: pacientes.filter(p => p.status === 'nao_fechou').length + 
                    agendamentos.filter(a => a.status === 'nao_fechou').length,
        naoCompareceram: agendamentos.filter(a => a.status === 'nao_compareceu').length,
        reagendados: pacientes.filter(p => p.status === 'reagendado').length + 
                    agendamentos.filter(a => a.status === 'reagendado').length
      };

      setPipelineStats(pipelineData);
    } catch (error) {
      console.error('Erro ao carregar estatÃ­sticas do pipeline:', error);
    }
  };

  const hoje = new Date().toLocaleDateString('pt-BR');

  // Calcular taxa de conversÃ£o
  const taxaConversao = pipelineStats.leads > 0 
    ? Math.round((pipelineStats.fechados / pipelineStats.leads) * 100)
    : 0;

  if (loading) {
    return (
      <div>
        <div className="page-header">
          <h1 className="page-title">ğŸ“Š Dashboard</h1>
          <p className="page-subtitle">Carregando dados...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">ğŸ“Š {isAdmin ? 'Dashboard Administrativo' : 'Meu Dashboard'}</h1>
        <p className="page-subtitle">
          {isAdmin 
            ? `VisÃ£o geral da operaÃ§Ã£o - ${hoje}` 
            : `Seus dados, ${user?.nome} - ${hoje}`
          }
        </p>
      </div>

      {/* Pipeline de Vendas */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">ğŸ¯ Pipeline de Vendas</h2>
          <div style={{ fontSize: '1.2rem', fontWeight: '600', color: '#10b981' }}>
            Taxa de ConversÃ£o: {taxaConversao}%
          </div>
        </div>

        <div className="stats-grid">
          <div className="stat-card" style={{ borderLeftColor: '#fbbf24' }}>
            <div className="stat-number" style={{ color: '#fbbf24' }}>{pipelineStats.leads}</div>
            <div className="stat-label">ğŸ” Leads</div>
          </div>
