                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  {editingClinica ? 'ðŸ’¾ Atualizar ClÃ­nica' : 'ðŸ’¾ Cadastrar ClÃ­nica'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Clinicas;
